package test0605;
//循环wile   do while for
public class Test1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;//初始值  （循环条件） {循环操作} i++迭代
		while(i<=100){
			System.out.println(i+"study");
			i++;
		}
		for(i=1;i<100;i++){
			System.out.println(i+"study");
		}
		System.out.println(i);
	}

}
